// posts/1/comments
import axios from 'axios'
const Post = (data) => (

  <ul>
    {data.map(d => <li>{d.body}</li>)}
  </ul>
)

Post.getInitialProps = async ({ query }) => {
  const { data } = await axios.get(`https://jsonplaceholder.typicode.com/posts/${query.id}/comments`)

  return { data }
}

export default Post
