// posts/1/comments
import axios from 'axios'
const Post = ({ data, id }) => (
  <>
    <div>id:{id}</div>
    {data.map(d =>
      <Comment
        email={d.email}
        body={d.body}
      />)}
  </>
)

const Comment = ({ email, body }) => (
  <>
    <div>{email}</div>
    <div>{body}</div>
  </>
)

Post.getInitialProps = async ({ query }) => {
  const { data } = await axios.get(`https://jsonplaceholder.typicode.com/comments?postId=${query.id}`)

  return { ...query, data }
}

export default Post
