// posts/1/comments
import axios from 'axios'
const Post = ({ id }) => (
  <h1>
    Post {id}
  </h1>
)

Post.getInitialProps = async ({ query }) => {
  const {data} = await axios.get(`https://jsonplaceholder.typicode.com/posts/{id}/comments`)
  console.log("Post.getInitialProps -> data", data)

  return { id: query.id }
}

export default Post
